import { useAuth0 } from "@auth0/auth0-react";
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Profile() {
  const [tableData, setTableData] = useState([]);

  const { isAuthenticated, user } = useAuth0();
  const userId = user?.sub;


  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://api.lgbusiness.net/api/v1/events/purchase?user_id=${userId}&limit=10&offset=0`);
        console.log(response.data);
        setTableData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    if (userId) {
      fetchData();
    }
  }, [userId]);

  return (
    <div>

      {isAuthenticated && (
        <>
        <h1>Bienvenido, {user.name}!!</h1>
        <p1>id usuario {userId} </p1>
        <h2>Mis compras</h2>
        <table>
            <thead>
              <tr>
                <th>ID Compra</th>
                <th>Event ID</th>
                <th>Cantidad</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {tableData.length > 0 && tableData.map((item) => (
                <tr key={item._id}>
                  <td>{item._id}</td>
                  <td>{item.eventId}</td>
                  <td>{item.quantity}</td>
                  <td>{item.status}</td>
                </tr>
              ))}
            </tbody>
        </table>
        </>
      )} 

      {!isAuthenticated && (
        <h1>Por favor ingresa para ver tus compras</h1>
      )}

    </div>
  );

              }
export default Profile;
