        <div> <p>Hello {name} </p></div>
      }

      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={Home} />
          <Route path="/about" element={About} />
        </Routes>

      </Router>
    </>
  );
}

export default App;
