import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import "leaflet/dist/leaflet.css";
import { Icon } from "leaflet";

function Map() {

  const [data, setData] = useState([]);
  const [markers, setMarkers] = useState([]);

  const icon = new Icon({
    iconUrl: require("./img/marker.png"),
    iconSize: [25, 25]
  });

  useEffect(() => {
    axios.get('https://api.lgbusiness.net/api/v1/events/')
      .then(response => {
        setData(response.data.instances);
        setMarkers(response.data.instances.map(instance => ({
          lat: instance.latitude,
          lng: instance.longitude,
          name: instance.name
        })));
      });
  }, []);

return (
  <>
  <MapContainer center={[-33.455275555427804	, -70.6605295633026]} zoom={15} style={{ height: '100vh', width: '100%'}}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" 
       attribution='Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors'/>
      {markers.map(marker => (
        <Marker key={marker.name} position={[marker.lat, marker.lng] }>
          <Popup>{marker.name}</Popup>
        </Marker>
      ))}
    </MapContainer>
  </>
)



}

export default Map;
