import { useAuth0 } from "@auth0/auth0-react";
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Profile() {
  const [tableData, setTableData] = useState([]);

  const { isAuthenticated, user } = useAuth0();
  const userId = user?.sub;


  const { getAccessTokenSilently } = useAuth0();
  const [token, setToken] = useState(null);

  useEffect(() => {
    async function fetchToken() {
      const token = await getAccessTokenSilently();
      setToken(token);
    }
    fetchToken();
  }, [getAccessTokenSilently]);

  useEffect(() => {
    if (!token) return; // Return early if the token is null
    axios.get(`https://api.lgbusiness.net/api/v1/events/purchase?user_id=${userId}&limit=10&offset=0`, {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}` // Replace with your actual bearer token
      },

    })
      .then(response => setTableData(response.data.instances));
  }, [token]);




  return (
    <div>

      {isAuthenticated && (
        <>
        <h1>Bienvenido, {user.name}!!</h1>
        <p>id usuario {userId} </p>
        <h2>Mis compras</h2>
        <table>
            <thead>
              <tr>
                <th>ID Compra</th>
                <th>Event ID</th>
                <th>Cantidad</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {tableData.length > 0 && tableData.map((item) => (
                <tr key={item._id}>
                  <td>{item._id}</td>
                  <td>{item.eventId}</td>
                  <td>{item.quantity}</td>
                  <td>{item.status}</td>
                </tr>
              ))}
            </tbody>
        </table>
        </>
      )} 

      {!isAuthenticated && (
        <h1>Por favor ingresa para ver tus compras</h1>
      )}

    </div>
  );

              }
export default Profile;
